import {useParams} from "react-router-dom";
import {useState, useEffect}  from  "react"
import axios from "axios";
//import {axiosInstance} from "../network/index";



function Showmovie(props) {

	const params  = useParams()
	console.log(params, "params")// params link 
	console.log(props, "props")

  const [movies, setmovies] = useState([])

	useEffect(() => {
		axios
		.get(`https://api.themoviedb.org/3/movie/${params.id}?api_key=1329a0f37fb81007926e9991415232ac`)
		.then((res)=> {
			console.log(res.data , "gggg")
			setmovies(res.data)

		})
		.catch((err) => {
			console.log(err)
		})
	}, [])






   return (
   <>
   <h1> Show Movie Details </h1>
  <p> {movies.poster_path}  </p>
   <p> {movies.overview}  </p>
  <p> {movies.id}  </p>
 </>
 );
}


  export default Showmovie;
